// embed.min.js
(function () {
  const config = window.embeddedChatbotConfig || {};
  const chatbotId = config.chatbotId;
  const domain = config.domain;

  if (!chatbotId || !domain) {
    console.error("Chatbot configuration is missing");
    return;
  }

  const chatContainer = document.createElement("div");
  chatContainer.id = "chat-widget-container";
  document.body.appendChild(chatContainer);

  const widget = document.createElement("div");
  widget.id = "chat-widget";
  chatContainer.appendChild(widget);

  // Add styles to the widget container
  chatContainer.style.position = "fixed";
  chatContainer.style.bottom = "20px";
  chatContainer.style.right = "20px";
  chatContainer.style.zIndex = "9999";

  // Render the "Hola Mundo" message
  widget.innerHTML =
    '<div style="width: 300px; height: 400px; background-color: white; border: 1px solid black; padding: 10px;">Hola Mundo</div>';
})();
